public class TasquesUD5_9 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 100; i++) {
			if (i % 2 == 0 && i % 3 == 0) {
				System.out.println("El numero " + i + " es divisible entre 2 y 3");
			}
		}
	}

}
